// App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Logout from "./pages/Logout";
import ProtectedRoute from "./components/ProtectedRoute";
import axios from "axios";
import "./App.css";

const Navbar = () => {
  return (
    <nav>
      <a href="/">Home</a> | <a href="/login">Login</a> |{" "}
      <a href="/register">Register</a> | <a href="/logout">Logout</a> |{" "}
      <a href="/manage">Manage Posts</a>
    </nav>
  );
};

function App() {
  // Fetch posts and store them in state (even if it's an empty array initially)
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        // Ensure you attach the token if required
        const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/posts`);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/" element={<h1>Welcome to Blog Management</h1>} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/logout" element={<Logout />} />

          {/* Protected Routes */}
          <Route element={<ProtectedRoute />}>
            <Route
              path="/manage"
              element={
                <div>
                  <h1>Manage Posts</h1>
                  <PostForm />
                  {/* Pass the posts array as a prop */}
                  <PostList posts={posts} />
                </div>
              }
            />
          </Route>

          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
