import React, { useState, useContext } from 'react';
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';
import AuthContext from '../context/AuthContext';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;
const TINYMCE_API_KEY = process.env.REACT_APP_TINYMCE_API_KEY;

const PostForm = ({ onPostCreated }) => {
  const { token, user } = useContext(AuthContext);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Debugging: Log token, user, and API base URL
    console.log("üîç Token:", token);
    console.log("üîç User:", user);
    console.log("üîç API Base URL:", API_BASE_URL);

    // Ensure the user is authenticated before attempting to create a post
    if (!token || !user) {
      alert("‚ùå You are not authenticated. Please log in.");
      return;
    }

    // Validate that title and content are not empty
    if (!title.trim() || !content.trim()) {
      alert("‚ùå Title and content cannot be empty.");
      return;
    }

    try {
      // Send the post request to the backend
      const response = await axios.post(
        `${API_BASE_URL}/posts`,
        { title, content, authorId: user.id },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      // Success: Log response data
      console.log("‚úÖ Post created successfully:", response.data);
      alert("‚úÖ Post created successfully!");

      setTitle('');
      setContent('');
      if (onPostCreated) onPostCreated(response.data);
    } catch (error) {
      // Debugging: Log full error object and response data
      console.error("‚ùå Full Error Object:", error);

      if (error.response) {
        console.error("‚ùå Error Response:", error.response);
        // Extract and display the error message properly
        const errorMessage = error.response.data?.message || JSON.stringify(error.response.data) || "Unknown error";
        alert(`‚ùå Failed to create post: ${errorMessage}`);
      } else {
        alert("‚ùå Failed to create post: Unknown error");
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create a New Post</h3>
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Content:</label>
        <Editor
          apiKey={TINYMCE_API_KEY}
          value={content}
          onEditorChange={(newContent) => setContent(newContent)}
          init={{
            height: 300,
            menubar: false,
            plugins: 'link image code',
            toolbar:
              'undo redo | formatselect | bold italic | alignleft aligncenter alignright | code',
          }}
        />
      </div>
      <button type="submit">Create Post</button>
    </form>
  );
};

export default PostForm;
