import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import PostForm from './PostForm'; // Import PostForm to use it in the PostList
import "../App.css";

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostList = ({ onPostUpdated, onPostDeleted }) => {
  const { token, user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false); // Manage form visibility

  // Update local posts if the initial posts prop changes
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/posts`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPosts(response.data.filter(post => post.authorId === user.id));
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    fetchPosts();
  }, [token, user]);

  // Toggle the visibility of the form.
  const toggleForm = () => {
    setIsFormVisible(!isFormVisible);
  };

  // Edit post logic
  const handleEditClick = (post) => {
    // Implement the edit post logic here
    console.log("Editing post:", post);
  };

  // Delete post logic
  const handleDelete = async (postId) => {
    try {
      await axios.delete(`${API_BASE_URL}/posts/${postId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts(posts.filter(post => post.id !== postId)); // Remove the deleted post from the state
      console.log("✅ Post deleted.");
    } catch (error) {
      console.error('Failed to delete post:', error);
      alert("❌ Failed to delete post.");
    }
  };

  return (
    <div className='container'>
      <h2>All Posts</h2>
      {/* Add Create post button */}
      <button onClick={toggleForm} className="btn create-post-btn">
        {isFormVisible ? 'Cancel' : 'Create Post'}
      </button>

      {/* Conditionally render PostForm */}
      {isFormVisible && <PostForm onPostCreated={(newPost) => setPosts([newPost, ...posts])} />}

      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-card">
            {/* Edit and delete logic */}
            <h3>{post.title}</h3>
            <p className="post-date">
              <strong>Date Posted:</strong> {new Date(post.createdAt).toLocaleString()}
            </p>
            <div
              className="post-content"
              dangerouslySetInnerHTML={{ __html: post.content.replace(/<\/?p>/g, '') }}
            />
            <button onClick={() => handleEditClick(post)} className="btn btn-edit">
              Edit
            </button>
            <button onClick={() => handleDelete(post.id)} className="btn btn-delete">
              Delete
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
