import React, { useState, useEffect, useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import AuthContext from "./context/AuthContext"; // import AuthContext
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Logout from "./pages/Logout";
import ProtectedRoute from "./components/ProtectedRoute";
import axios from "axios";
import Navbar from "./styles/Navbar.css"; // Import the Navbar component
import "../App.css";
import "./styles/Navbar.css";
function App() {
  const { token, user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [showForm, setShowForm] = useState(false); // state to toggle PostForm

  useEffect(() => {
    const fetchPosts = async () => {
      if (!token) return; // Wait until token is available
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_BASE_URL}/posts`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, [token]);

  // Callback to update posts when a new post is created
  const handlePostCreated = (newPost) => {
    setPosts((prevPosts) => [newPost, ...prevPosts]);
    setShowForm(false); // Hide the form after a successful post creation
  };

  // Filter posts to show only those created by the logged-in user
  const userPosts = user ? posts.filter((post) => post.authorId === user.id) : [];

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<h1>Welcome to Blog Management</h1>} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/logout" element={<Logout />} />

        {/* Protected Routes */}
        <Route element={<ProtectedRoute />}>
          <Route
            path="/manage"
            element={
              <div>
                <h1>Manage Posts</h1>
                <button 
                  onClick={() => setShowForm(!showForm)}
                  style={{
                    padding: "10px 20px",
                    marginBottom: "20px",
                    backgroundColor: "var(--success-color)",
                    color: "#fff",
                    border: "none",
                    borderRadius: "var(--border-radius-base)",
                    cursor: "pointer"
                  }}
                >
                  {showForm ? "Close Form" : "Create Post"}
                </button>
                {showForm && <PostForm onPostCreated={handlePostCreated} />}
                <PostList posts={userPosts} />
              </div>
            }
          />
        </Route>

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
