import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';
import AuthContext from '../context/AuthContext';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;
const TINYMCE_API_KEY = process.env.REACT_APP_TINYMCE_API_KEY;

const PostForm = ({ onPostCreated }) => {
  const { token: contextToken, user } = useContext(AuthContext);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [token, setToken] = useState(contextToken || localStorage.getItem("token"));

  useEffect(() => {
    setToken(contextToken || localStorage.getItem("token"));
  }, [contextToken]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ‚úÖ Debugging: Log token and user
    console.log("üîç Token in frontend:", token);
    console.log("üîç User in frontend:", user);

    // ‚úÖ Check authentication
    if (!token || !user) {
      alert("‚ùå You are not authenticated. Please log in.");
      return;
    }

    // ‚úÖ Ensure title and content are not empty
    if (!title.trim() || !content.trim()) {
      alert("‚ùå Title and content cannot be empty.");
      return;
    }

    try {
      const response = await axios.post(
        `${API_BASE_URL}/posts`,
        { title, content, authorId: user.id },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      console.log("‚úÖ Post created successfully:", response.data);
      alert("‚úÖ Post created successfully!");

      setTitle('');
      setContent('');
      if (onPostCreated) onPostCreated(response.data);
    } catch (error) {
      console.error("‚ùå Error creating post:", error.response?.data || error.message);
      alert(`‚ùå Failed to create post: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create a New Post</h3>
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Content:</label>
        <Editor
          apiKey={TINYMCE_API_KEY}
          value={content}
          onEditorChange={(newContent) => setContent(newContent)}
          init={{
            height: 300,
            menubar: false,
            plugins: 'link image code',
            toolbar:
              'undo redo | formatselect | bold italic | alignleft aligncenter alignright | code',
          }}
        />
      </div>
      <button type="submit">Create Post</button>
    </form>
  );
};

export default PostForm;
