// src/pages/Login.js
import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../context/AuthContext";

const Login = () => {
  const { login } = useContext(AuthContext);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false); // New state for loading
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      setError("Both email and password are required.");
      return;
    }

    setLoading(true); // Set loading to true before making the request
    try {
      await login({ email, password });
      navigate("/manage"); // Redirect on successful login
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false); // Reset loading state after request is done
    }
  };

  return (
    <div style={{ width: "300px", margin: "auto", padding: "20px", border: "1px solid #ddd", borderRadius: "5px" }}>
      <h2 style={{ textAlign: "center" }}>Login</h2>
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
      <form onSubmit={handleLogin} style={{ display: "flex", flexDirection: "column" }}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ padding: "10px", margin: "10px 0", border: "1px solid #ccc", borderRadius: "5px" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ padding: "10px", margin: "10px 0", border: "1px solid #ccc", borderRadius: "5px" }}
        />
        <button 
          type="submit" 
          style={{
            padding: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
          disabled={loading} // Disable button while loading
        >
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
      <div style={{ marginTop: "10px", textAlign: "center" }}>
        {loading && <span>Loading...</span>}
      </div>
    </div>
  );
};

export default Login;
