import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CommentList from './CommentList';

const PostList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:3000/posts');
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const togglePublishStatus = async (postId, published) => {
    try {
      await axios.put(`http://localhost:3000/posts/${postId}`, {
        published: !published,
      });
      // Refresh the posts list
      const response = await axios.get('http://localhost:3000/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('Error updating post:', error);
    }
  };

  return (
    <div>
      <h2>All Posts</h2>
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0' }}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            <p>
              <strong>Author:</strong> {post.authorId} | <strong>Status:</strong> {post.published ? 'Published' : 'Unpublished'}
            </p>
            <button onClick={() => togglePublishStatus(post.id, post.published)}>
              {post.published ? 'Unpublish' : 'Publish'}
            </button>
            <CommentList postId={post.id} />
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
