import React, { useState, useContext } from 'react';
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';
import AuthContext from '../context/AuthContext';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;
const TINYMCE_API_KEY = process.env.REACT_APP_TINYMCE_API_KEY;

const PostForm = ({ onPostCreated }) => {
  const { token, user } = useContext(AuthContext);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token || !user) {
      alert("❌ You are not authenticated. Please log in.");
      return;
    }

    if (!title.trim() || !content.trim()) {
      alert("❌ Title and content cannot be empty.");
      return;
    }

    try {
      const response = await axios.post(
        `${API_BASE_URL}/posts`,
        {
          title,
          content,
          authorId: user.id,
          published: false,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      console.log("✅ Post created successfully:", response.data);
      alert("✅ Post created successfully!");

      setTitle('');
      setContent('');

      // Directly update the parent component's post list after creating a new post
      if (onPostCreated) {
        onPostCreated(response.data);  // Pass new post to the parent
      }
    } catch (error) {
      console.error("❌ Full Error Object:", error);

      if (error.response) {
        console.error("❌ Error Response:", error.response);
        const errorMessage = error.response.data?.message || "Unknown error";
        alert(`❌ Failed to create post: ${errorMessage}`);
      } else {
        alert("❌ Failed to create post: Unknown error");
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create a New Post</h3>
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Content:</label>
        <Editor
          apiKey={TINYMCE_API_KEY}
          value={content}
          onEditorChange={(newContent) => setContent(newContent)}
          init={{
            height: 300,
            menubar: false,
            plugins: 'link image code',
            toolbar: 'undo redo | formatselect | bold italic | alignleft aligncenter alignright | code',
          }}
        />
      </div>
      <button type="submit">Create Post</button>
    </form>
  );
};

export default PostForm;
