import React, { useState, useContext } from 'react';
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';
import AuthContext from '../context/AuthContext';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;
const TINYMCE_API_KEY = process.env.REACT_APP_TINYMCE_API_KEY;

const PostForm = ({ onPostCreated }) => {
  const { token, user } = useContext(AuthContext);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Debug logs to inspect data before the API call.
    console.log("Creating post with the following details:");
    console.log("Title:", title);
    console.log("Content:", content);
    console.log("Author ID:", user?.id);
    console.log("Token:", token);

    try {
      const response = await axios.post(
        `${API_BASE_URL}/posts`,
        {
          title,
          content,
          authorId: user?.id,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      console.log("Post created successfully:", response.data);
      alert('Post created successfully!');
      setTitle('');
      setContent('');
      if (onPostCreated) onPostCreated(response.data);
    } catch (error) {
      console.error("Error creating post:", error.response || error.message);
      alert('Failed to create post.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create a New Post</h3>
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Content:</label>
        <Editor
          apiKey={TINYMCE_API_KEY}
          value={content}
          onEditorChange={(newContent) => setContent(newContent)}
          init={{
            height: 300,
            menubar: false,
            plugins: 'link image code',
            toolbar:
              'undo redo | formatselect | bold italic | alignleft aligncenter alignright | code',
          }}
        />
      </div>
      <button type="submit">Create Post</button>
    </form>
  );
};

export default PostForm;
