import React, { useState } from 'react';
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostForm = ({ onPostCreated }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_BASE_URL}/posts`, {
        title,
        content,
        authorId: 1, // Replace with actual user ID from authentication
      });

      alert('Post created successfully!');
      setTitle('');
      setContent('');
      onPostCreated(response.data);
    } catch (error) {
      console.error('Error creating post:', error);
      alert('Failed to create post.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create a New Post</h3>
      <div>
        <label>Title:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
      </div>
      <div>
        <label>Content:</label>
        <Editor
          apiKey="oqishh7cbvefurxk7ybkj47arlyddkrje4oy1t0c4qhvssnn"
          value={content}
          onEditorChange={(newContent) => setContent(newContent)}
          init={{ height: 300, menubar: false, plugins: 'link image code', toolbar: 'undo redo | formatselect | bold italic | alignleft aligncenter alignright | code' }}
        />
      </div>
      <button type="submit">Create Post</button>
    </form>
  );
};

export default PostForm;
