import React, { useState, useEffect, useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import AuthContext from "./context/AuthContext";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Logout from "./pages/Logout";
import ProtectedRoute from "./components/ProtectedRoute";
import axios from "axios";
import "./styles.css";

const Navbar = () => {
  return (
    <nav>
      <a href="/">Home</a> | <a href="/login">Login</a> |{" "}
      <a href="/register">Register</a> | <a href="/logout">Logout</a> |{" "}
      <a href="/manage">Manage Posts</a>
    </nav>
  );
};

function App() {
  const { token, user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [showPostForm, setShowPostForm] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      if (!token) return;
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_BASE_URL}/posts`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, [token]);

  const handlePostCreated = (newPost) => {
    setPosts((prevPosts) => [newPost, ...prevPosts]);
    setShowPostForm(false); // Hide form after submission
  };

  const togglePostForm = () => {
    setShowPostForm((prev) => !prev);
  };

  const userPosts = user ? posts.filter((post) => post.authorId === user.id) : [];

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<h1>Welcome to Blog Management</h1>} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/logout" element={<Logout />} />
        <Route element={<ProtectedRoute />}>
          <Route
            path="/manage"
            element={
              <div>
                <h1>Manage Posts</h1>
                <div className="post-form-container">
                  <button id="toggle-post-form" onClick={togglePostForm}>
                    {showPostForm ? "Hide Create Post" : "Create New Post"}
                  </button>
                  <PostForm
                    onPostCreated={handlePostCreated}
                    className={showPostForm ? "active" : ""}
                  />
                </div>
                <PostList posts={userPosts} />
              </div>
            }
          />
        </Route>
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
