import React, { useState } from 'react';
import axios from 'axios';
import CommentList from './CommentList';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostList = ({ posts, onPostUpdated, onPostDeleted }) => {
  // State to track which posts have expanded comments.
  const [expandedPosts, setExpandedPosts] = useState([]);
  // State to track which post is in edit mode along with its updated values.
  const [editingPost, setEditingPost] = useState(null);
  const [editValues, setEditValues] = useState({ title: '', content: '' });

  // Toggle published/unpublished status.
  const togglePublishStatus = async (postId, published) => {
    try {
      await axios.put(`${API_BASE_URL}/posts/${postId}`, { published: !published });
      onPostUpdated(postId, { published: !published });
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post status.');
    }
  };

  // Toggle the expansion of comments for a specific post.
  const toggleComments = (postId) => {
    setExpandedPosts((prevExpanded) =>
      prevExpanded.includes(postId)
        ? prevExpanded.filter((id) => id !== postId)
        : [...prevExpanded, postId]
    );
  };

  // Enable edit mode for a post.
  const handleEditClick = (post) => {
    setEditingPost(post.id);
    setEditValues({ title: post.title, content: post.content });
  };

  // Cancel editing.
  const cancelEdit = () => {
    setEditingPost(null);
    setEditValues({ title: '', content: '' });
  };

  // Handle changes in the edit form.
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditValues((prev) => ({ ...prev, [name]: value }));
  };

  // Submit updated post data.
  const handleEditSubmit = async (e, postId) => {
    e.preventDefault();
    try {
      const response = await axios.put(`${API_BASE_URL}/posts/${postId}`, {
        title: editValues.title,
        content: editValues.content,
      });
      onPostUpdated(postId, response.data);
      setEditingPost(null);
      setEditValues({ title: '', content: '' });
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post.');
    }
  };

  // Delete the post.
// Delete the post.
const handleDelete = async (postId) => {
  // Show a confirmation prompt before deletion.
  const confirmDelete = window.confirm('Are you sure you want to delete this post? This action cannot be undone.');
  if (!confirmDelete) return; // Exit if the user cancels.

  try {
    await axios.delete(`${API_BASE_URL}/posts/${postId}`);
    onPostDeleted(postId);
  } catch (error) {
    console.error('Error deleting post:', error);
    alert('Failed to delete post.');
  }
};

  return (
    <div>
      <h2>All Posts</h2>
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div
            key={post.id}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              margin: '10px 0',
              borderRadius: '8px',
              boxShadow: '2px 2px 10px rgba(0, 0, 0, 0.1)',
            }}
          >
            {editingPost === post.id ? (
              // Inline edit form.
              <form onSubmit={(e) => handleEditSubmit(e, post.id)}>
                <div>
                  <label>Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={editValues.title}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div>
                  <label>Content:</label>
                  <textarea
                    name="content"
                    value={editValues.content}
                    onChange={handleEditChange}
                    rows="5"
                    style={{ width: '100%' }}
                    required
                  />
                </div>
                <button type="submit" style={{ marginRight: '10px' }}>Save</button>
                <button type="button" onClick={cancelEdit}>Cancel</button>
              </form>
            ) : (
              <>
                <h3>{post.title}</h3>
                <div dangerouslySetInnerHTML={{ __html: post.content }} />
                <p>
                  <strong>Author:</strong> {post.authorId} | <strong>Status:</strong>{' '}
                  <span style={{ color: post.published ? 'green' : 'red', fontWeight: 'bold' }}>
                    {post.published ? 'Published' : 'Unpublished'}
                  </span>
                </p>
                {/* Publish/Unpublish button */}
                <button
                  onClick={() => togglePublishStatus(post.id, post.published)}
                  style={{
                    backgroundColor: post.published ? 'red' : 'green',
                    color: 'white',
                    border: 'none',
                    padding: '8px 12px',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginBottom: '10px',
                    marginRight: '10px'
                  }}
                >
                  {post.published ? 'Unpublish' : 'Publish'}
                </button>
                {/* Edit button */}
                <button
                  onClick={() => handleEditClick(post)}
                  style={{
                    backgroundColor: '#ffc107',
                    color: 'black',
                    border: 'none',
                    padding: '8px 12px',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginBottom: '10px',
                    marginRight: '10px'
                  }}
                >
                  Edit
                </button>
                {/* Delete button */}
                <button
                  onClick={() => handleDelete(post.id)}
                  style={{
                    backgroundColor: '#dc3545',
                    color: 'white',
                    border: 'none',
                    padding: '8px 12px',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginBottom: '10px'
                  }}
                >
                  Delete
                </button>
                {/* Button to toggle comment visibility */}
                <div style={{ marginTop: '10px' }}>
                  <button
                    onClick={() => toggleComments(post.id)}
                    style={{
                      backgroundColor: '#007bff',
                      color: 'white',
                      border: 'none',
                      padding: '5px 10px',
                      borderRadius: '50%',
                      fontSize: '18px',
                      cursor: 'pointer',
                    }}
                    title={expandedPosts.includes(post.id) ? "Hide comments" : "Show comments"}
                  >
                    {expandedPosts.includes(post.id) ? '-' : '+'}
                  </button>
                </div>
                {/* Render comments if the post is expanded */}
                {expandedPosts.includes(post.id) && (
                  <div style={{ marginTop: '10px' }}>
                    <CommentList postId={post.id} />
                  </div>
                )}
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
