import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CommentList from './CommentList';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostList = ({ posts, onPostUpdated, onPostDeleted }) => {
  const [expandedPosts, setExpandedPosts] = useState([]);
  const [editingPost, setEditingPost] = useState(null);
  const [editValues, setEditValues] = useState({ title: '', content: '' });
  const [filter, setFilter] = useState('published'); // Default filter for published posts

  // Filter posts based on published/unpublished status
  const filteredPosts = posts.filter((post) =>
    filter === 'published' ? post.published : !post.published
  );

  // Toggle published/unpublished status
  const togglePublishStatus = async (postId, published) => {
    try {
      await axios.put(`${API_BASE_URL}/posts/${postId}`, { published: !published });
      onPostUpdated(postId, { published: !published });
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post status.');
    }
  };

  // Toggle the expansion of comments for a specific post.
  const toggleComments = (postId) => {
    setExpandedPosts((prevExpanded) =>
      prevExpanded.includes(postId)
        ? prevExpanded.filter((id) => id !== postId)
        : [...prevExpanded, postId]
    );
  };

  // Enable edit mode for a post.
  const handleEditClick = (post) => {
    setEditingPost(post.id);
    setEditValues({ title: post.title, content: post.content });
  };

  // Cancel editing.
  const cancelEdit = () => {
    setEditingPost(null);
    setEditValues({ title: '', content: '' });
  };

  // Handle changes in the edit form.
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditValues((prev) => ({ ...prev, [name]: value }));
  };

  // Submit updated post data.
  const handleEditSubmit = async (e, postId) => {
    e.preventDefault();
    try {
      const response = await axios.put(`${API_BASE_URL}/posts/${postId}`, {
        title: editValues.title,
        content: editValues.content,
      });
      onPostUpdated(postId, response.data);
      setEditingPost(null);
      setEditValues({ title: '', content: '' });
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post.');
    }
  };

  // Delete the post with a confirmation warning.
  const handleDelete = async (postId) => {
    const confirmDelete = window.confirm(
      'Are you sure you want to delete this post? This action cannot be undone.'
    );
    if (!confirmDelete) return;

    try {
      await axios.delete(`${API_BASE_URL}/posts/${postId}`);
      onPostDeleted(postId);
    } catch (error) {
      console.error('Error deleting post:', error);
      alert('Failed to delete post.');
    }
  };

  // Toggle the filter between published and unpublished
  const toggleFilter = (status) => {
    setFilter(status);
  };

  return (
    <div>
      <h2>All Posts</h2>

      {/* Toggle to switch between published and unpublished posts */}
      <div>
        <button
          onClick={() => toggleFilter('published')}
          className={filter === 'published' ? 'btn-active' : 'btn'}
        >
          Published
        </button>
        <button
          onClick={() => toggleFilter('unpublished')}
          className={filter === 'unpublished' ? 'btn-active' : 'btn'}
        >
          Unpublished
        </button>
      </div>

      {filteredPosts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        filteredPosts.map((post) => (
          <div key={post.id} className="post-card">
            {editingPost === post.id ? (
              <form onSubmit={(e) => handleEditSubmit(e, post.id)} className="post-edit-form">
                <div>
                  <label>Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={editValues.title}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div>
                  <label>Content:</label>
                  <textarea
                    name="content"
                    value={editValues.content}
                    onChange={handleEditChange}
                    rows="5"
                    required
                  ></textarea>
                </div>
                <button type="submit" className="btn">Save</button>
                <button type="button" className="btn" onClick={cancelEdit}>Cancel</button>
              </form>
            ) : (
              <>
                <h3>{post.title}</h3>
                <div className="post-content" dangerouslySetInnerHTML={{ __html: post.content }} />
                <p className="post-meta">
                  <strong>Author:</strong> {post.authorId} | <strong>Status:</strong>{' '}
                  <span
                    style={{
                      color: post.published ? 'var(--success-color)' : 'var(--danger-color)',
                      fontWeight: 'bold',
                    }}
                  >
                    {post.published ? 'Published' : 'Unpublished'}
                  </span>
                </p>

                <button
                  onClick={() => togglePublishStatus(post.id, post.published)}
                  className={post.published ? 'btn btn-unpublish' : 'btn btn-publish'}
                >
                  {post.published ? 'Unpublish' : 'Publish'}
                </button>
                <button onClick={() => handleEditClick(post)} className="btn btn-edit">Edit</button>
                <button onClick={() => handleDelete(post.id)} className="btn btn-delete">Delete</button>

                {/* Toggle for showing comments */}
                <div className="comment-toggle-container">
                  <span className="comment-count">
                    Comments {post.comments ? post.comments.length : 0}
                  </span>

                  {post.comments && post.comments.length > 0 && (
                    <button onClick={() => toggleComments(post.id)} className="comment-toggle-btn">
                      {expandedPosts.includes(post.id) ? 'Hide' : 'Show'}
                    </button>
                  )}
                </div>

                {expandedPosts.includes(post.id) && post.comments && (
                  <div className="comment-list">
                    <CommentList postId={post.id} />
                  </div>
                )}
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
