import React, { useState, useEffect, useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import AuthContext from "./context/AuthContext"; // import AuthContext
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Logout from "./pages/Logout";
import ProtectedRoute from "./components/ProtectedRoute";
import axios from "axios";
import "./App.css";

const Navbar = () => {
  return (
    <nav>
      <a href="/">Home</a> | <a href="/login">Login</a> |{" "}
      <a href="/register">Register</a> | <a href="/logout">Logout</a> |{" "}
      <a href="/manage">Manage Posts</a>
    </nav>
  );
};

function App() {
  const { token, user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      if (!token) return; // Wait until token is available
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_BASE_URL}/posts`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, [token]);

  // Callback to update posts when a new post is created
  const handlePostCreated = (newPost) => {
    setPosts((prevPosts) => [newPost, ...prevPosts]);
  };

  // Callback to update a post in state when updated
  const handlePostUpdated = (postId, updatedPost) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) => (post.id === postId ? updatedPost : post))
    );
  };

  // Callback to remove a post when deleted
  const handlePostDeleted = (postId) => {
    setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId));
  };

  // Filter posts to show only those created by the logged-in user
  const userPosts = user ? posts.filter((post) => post.authorId === user.id) : [];

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<h1>Welcome to Blog Management</h1>} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/logout" element={<Logout />} />

        {/* Protected Routes */}
        <Route element={<ProtectedRoute />}>
          <Route
            path="/manage"
            element={
              <div>
                <h1>Manage Posts</h1>
                <PostForm onPostCreated={handlePostCreated} />
                <PostList
                  posts={userPosts}
                  onPostUpdated={handlePostUpdated}
                  onPostDeleted={handlePostDeleted}
                />
              </div>
            }
          />
        </Route>

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
