import React, { useState } from 'react';
import axios from 'axios';
import CommentList from './CommentList';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostList = ({ posts, onPostUpdated }) => {
  // State to track which posts have expanded comments.
  const [expandedPosts, setExpandedPosts] = useState([]);

  const togglePublishStatus = async (postId, published) => {
    try {
      // When the button is clicked, update the published status.
      await axios.put(`${API_BASE_URL}/posts/${postId}`, { published: !published });
      onPostUpdated(postId, !published);
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post status.');
    }
  };

  // Toggle the expansion of comments for a specific post.
  const toggleComments = (postId) => {
    setExpandedPosts((prevExpanded) =>
      prevExpanded.includes(postId)
        ? prevExpanded.filter((id) => id !== postId)
        : [...prevExpanded, postId]
    );
  };

  return (
    <div>
      <h2>All Posts</h2>
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div
            key={post.id}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              margin: '10px 0',
              borderRadius: '8px',
              boxShadow: '2px 2px 10px rgba(0, 0, 0, 0.1)',
            }}
          >
            <h3>{post.title}</h3>
            <div dangerouslySetInnerHTML={{ __html: post.content }} />
            <p>
              <strong>Author:</strong> {post.authorId} | <strong>Status:</strong>{' '}
              <span style={{ color: post.published ? 'green' : 'red', fontWeight: 'bold' }}>
                {post.published ? 'Published' : 'Unpublished'}
              </span>
            </p>
            {/* The publish/unpublish button */}
            <button
              onClick={() => togglePublishStatus(post.id, post.published)}
              style={{
                backgroundColor: post.published ? 'red' : 'green',
                color: 'white',
                border: 'none',
                padding: '8px 12px',
                borderRadius: '5px',
                cursor: 'pointer',
                marginBottom: '10px',
              }}
            >
              {post.published ? 'Unpublish' : 'Publish'}
            </button>
            {/* Button to toggle comment visibility */}
            <div style={{ marginTop: '10px' }}>
              <button
                onClick={() => toggleComments(post.id)}
                style={{
                  backgroundColor: '#007bff',
                  color: 'white',
                  border: 'none',
                  padding: '5px 10px',
                  borderRadius: '50%',
                  fontSize: '18px',
                  cursor: 'pointer',
                }}
                title={expandedPosts.includes(post.id) ? "Hide comments" : "Show comments"}
              >
                {expandedPosts.includes(post.id) ? '-' : '+'}
              </button>
            </div>
            {/* Render comments if the post is expanded */}
            {expandedPosts.includes(post.id) && (
              <div style={{ marginTop: '10px' }}>
                <CommentList postId={post.id} />
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
