import React from 'react';
import axios from 'axios';
import CommentList from './CommentList';

const API_URL = 'http://localhost:3000/posts';

const PostList = ({ posts, setPosts }) => {
  const togglePublishStatus = async (postId, published) => {
    try {
      await axios.put(`${API_URL}/${postId}`, { published: !published });

      // Update state locally
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post.id === postId ? { ...post, published: !published } : post
        )
      );
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post status.');
    }
  };

  return (
    <div>
      <h2>All Posts</h2>
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0', borderRadius: '8px' }}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            <p>
              <strong>Author:</strong> {post.authorId} | <strong>Status:</strong>{' '}
              <span style={{ color: post.published ? 'green' : 'red', fontWeight: 'bold' }}>
                {post.published ? 'Published' : 'Unpublished'}
              </span>
            </p>
            <button
              onClick={() => togglePublishStatus(post.id, post.published)}
              style={{ backgroundColor: post.published ? 'red' : 'green', color: 'white', padding: '8px 12px' }}
            >
              {post.published ? 'Unpublish' : 'Publish'}
            </button>
            <CommentList postId={post.id} />
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
