import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const CommentList = ({ postId }) => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const fetchComments = async () => {
      try {
        // Fetch all comments from the endpoint
        const response = await axios.get(`${API_BASE_URL}/comments`);
        // Filter only the comments that belong to the current post
        const postComments = response.data.filter(comment => comment.postId === postId);
        setComments(postComments);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchComments();
  }, [postId]);

  const deleteComment = async (commentId) => {
    try {
      await axios.delete(`${API_BASE_URL}/comments/${commentId}`);
      setComments(comments.filter((comment) => comment.id !== commentId));
    } catch (error) {
      console.error('Error deleting comment:', error);
    }
  };

  return (
    <div>
      <h4>Comments</h4>
      {comments.length === 0 ? (
        <p>No comments found.</p>
      ) : (
        comments.map((comment) => (
          <div 
            key={comment.id} 
            style={{ margin: '10px 0', padding: '5px', border: '1px solid #eee' }}
          >
            <p>{comment.content}</p>
            <p>
              <strong>By:</strong> {comment.username || 'Anonymous'} | <strong>On:</strong>{' '}
              {new Date(comment.createdAt).toLocaleString()}
            </p>
            <button onClick={() => deleteComment(comment.id)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default CommentList;
