import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CommentList from './CommentList';

const API_URL = 'http://localhost:3000/posts'; // Change port if needed

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true); // Track loading state
  const [error, setError] = useState(null); // Store fetch errors

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        console.log('Fetching posts from:', API_URL);
        const response = await axios.get(API_URL);
        console.log('Fetched posts:', response.data);
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error.message);
        setError(`Failed to fetch posts: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  const togglePublishStatus = async (postId, published) => {
    try {
      await axios.put(`${API_URL}/${postId}`, { published: !published });

      // Update state locally instead of making another API call
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post.id === postId ? { ...post, published: !published } : post
        )
      );
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post status.');
    }
  };

  return (
    <div>
      <h2>All Posts</h2>

      {loading ? (
        <p>Loading posts...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div
            key={post.id}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              margin: '10px 0',
              borderRadius: '8px',
              boxShadow: '2px 2px 10px rgba(0, 0, 0, 0.1)',
            }}
          >
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            <p>
              <strong>Author:</strong> {post.authorId} |{' '}
              <strong>Status:</strong>{' '}
              <span
                style={{
                  color: post.published ? 'green' : 'red',
                  fontWeight: 'bold',
                }}
              >
                {post.published ? 'Published' : 'Unpublished'}
              </span>
            </p>
            <button
              onClick={() => togglePublishStatus(post.id, post.published)}
              style={{
                backgroundColor: post.published ? 'red' : 'green',
                color: 'white',
                border: 'none',
                padding: '8px 12px',
                borderRadius: '5px',
                cursor: 'pointer',
                marginBottom: '10px',
              }}
            >
              {post.published ? 'Unpublish' : 'Publish'}
            </button>
            <CommentList postId={post.id} />
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
