import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import CommentList from './CommentList';
import AuthContext from '../context/AuthContext';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostList = ({ posts: initialPosts = [], onPostUpdated, onPostDeleted }) => {
  const [posts, setPosts] = useState(initialPosts);
  const [expandedPosts, setExpandedPosts] = useState([]);
  const [editingPost, setEditingPost] = useState(null);
  const [editValues, setEditValues] = useState({ title: '', content: '' });

  // Sync local posts with prop changes
  useEffect(() => {
    setPosts(initialPosts);
  }, [initialPosts]);

  // Listen for new post events
  useEffect(() => {
    const handleNewPost = (e) => {
      setPosts((prevPosts) => [e.detail, ...prevPosts]);
    };
    window.addEventListener('newPostCreated', handleNewPost);
    return () => window.removeEventListener('newPostCreated', handleNewPost);
  }, []);

  // Toggle publish/unpublish status
  const togglePublishStatus = async (postId, currentPublished) => {
    try {
      const validToken = await refreshToken(); // Ensure token is valid
      const response = await axios.put(
        `${API_BASE_URL}/posts/${postId}`,
        { published: !currentPublished },
        {
          headers: {
            Authorization: `Bearer ${validToken}`,
            'Content-Type': 'application/json',
          },
        }
      );
      console.log('Publish status updated:', response.data);
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post.id === postId ? { ...post, published: response.data.published } : post
        )
      );
      if (onPostUpdated) onPostUpdated(postId, response.data);
    } catch (error) {
      console.error('Error toggling publish status:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
      });
      alert(`Failed to update post status: ${error.response?.data?.message || error.message}`);
    }
  };

  // Toggle comments visibility
  const toggleComments = (postId) => {
    setExpandedPosts((prev) =>
      prev.includes(postId) ? prev.filter((id) => id !== postId) : [...prev, postId]
    );
  };

  // Start editing a post
  const startEditing = (post) => {
    setEditingPost(post.id);
    setEditValues({ title: post.title, content: post.content });
  };

  // Cancel editing
  const cancelEditing = () => {
    setEditingPost(null);
    setEditValues({ title: '', content: '' });
  };

  // Handle edit form input changes
  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setEditValues((prev) => ({ ...prev, [name]: value }));
  };

  // Submit edited post
  const submitEdit = async (e, postId) => {
    e.preventDefault();
    try {
      const validToken = await refreshToken(); // Ensure token is valid
      const currentPost = posts.find((p) => p.id === postId);
      const response = await axios.put(
        `${API_BASE_URL}/posts/${postId}`,
        {
          title: editValues.title,
          content: editValues.content,
          published: currentPost.published, // Preserve current status
        },
        {
          headers: {
            Authorization: `Bearer ${validToken}`,
            'Content-Type': 'application/json',
          },
        }
      );
      console.log('Post updated:', response.data);
      setPosts((prevPosts) =>
        prevPosts.map((post) => (post.id === postId ? response.data : post))
      );
      if (onPostUpdated) onPostUpdated(postId, response.data);
      setEditingPost(null);
      setEditValues({ title: '', content: '' });
    } catch (error) {
      console.error('Error updating post:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
      });
      alert(`Failed to update post: ${error.response?.data?.message || error.message}`);
    }
  };

  // Delete a post
  const deletePost = async (postId) => {
    try {
      const validToken = await refreshToken(); // Ensure token is valid
      const postResponse = await axios.get(`${API_BASE_URL}/posts/${postId}`, {
        headers: { Authorization: `Bearer ${validToken}` },
      });
      const post = postResponse.data;

      if (post.comments?.length > 0) {
        alert('Cannot delete post with comments. Delete comments first.');
        return;
      }

      if (!window.confirm('Are you sure you want to delete this post?')) return;

      await axios.delete(`${API_BASE_URL}/posts/${postId}`, {
        headers: { Authorization: `Bearer ${validToken}` },
      });
      console.log('Post deleted:', postId);
      setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId));
      if (onPostDeleted) onPostDeleted(postId);
    } catch (error) {
      console.error('Error deleting post:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
      });
      alert(`Failed to delete post: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div className="post-list">
      <h2>All Posts</h2>
      {posts.length === 0 ? (
        <p>No posts available.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-card">
            {editingPost === post.id ? (
              <form onSubmit={(e) => submitEdit(e, post.id)} className="edit-form">
                <div>
                  <label>Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={editValues.title}
                    onChange={handleEditInputChange}
                    required
                  />
                </div>
                <div>
                  <label>Content:</label>
                  <textarea
                    name="content"
                    value={editValues.content}
                    onChange={handleEditInputChange}
                    rows="5"
                    required
                  />
                </div>
                <button type="submit" className="btn save-btn">
                  Save
                </button>
                <button type="button" className="btn cancel-btn" onClick={cancelEditing}>
                  Cancel
                </button>
              </form>
            ) : (
              <>
                <h3>{post.title}</h3>
                <div
                  className="post-content"
                  dangerouslySetInnerHTML={{ __html: post.content }}
                />
                <p className="post-meta">
                  <strong>Author:</strong> {post.authorId} |{' '}
                  <strong>Status:</strong>{' '}
                  <span
                    style={{
                      color: post.published ? 'green' : 'red',
                      fontWeight: 'bold',
                    }}
                  >
                    {post.published ? 'Published' : 'Unpublished'}
                  </span>
                </p>
                <div className="post-actions">
                  <button
                    onClick={() => togglePublishStatus(post.id, post.published)}
                    className={`btn ${post.published ? 'unpublish-btn' : 'publish-btn'}`}
                  >
                    {post.published ? 'Unpublish' : 'Publish'}
                  </button>
                  <button onClick={() => startEditing(post)} className="btn edit-btn">
                    Edit
                  </button>
                  <button onClick={() => deletePost(post.id)} className="btn delete-btn">
                    Delete
                  </button>
                </div>
                <div className="comments-section">
                  <span className="comment-count">
                    Comments: {post.comments?.length || 0}
                  </span>
                  {post.comments?.length > 0 && (
                    <button
                      onClick={() => toggleComments(post.id)}
                      className="btn toggle-comments-btn"
                    >
                      {expandedPosts.includes(post.id) ? 'Hide' : 'Show'} Comments
                    </button>
                  )}
                </div>
                {expandedPosts.includes(post.id) && post.comments && (
                  <CommentList postId={post.id} />
                )}
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
