import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import CommentList from './CommentList';
import AuthContext from '../context/AuthContext';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const PostList = ({ posts: initialPosts = [], onPostUpdated, onPostDeleted }) => {
  const { token } = useContext(AuthContext);
  const [posts, setPosts] = useState(initialPosts);
  const [expandedPosts, setExpandedPosts] = useState([]);
  const [editingPost, setEditingPost] = useState(null);
  const [editValues, setEditValues] = useState({ title: '', content: '' });

  // Update local posts if the initial posts prop changes
  useEffect(() => {
    setPosts(initialPosts);
  }, [initialPosts]);

  // Listen for new post events to update the list immediately
  useEffect(() => {
    const handleNewPost = (e) => {
      setPosts((prevPosts) => [e.detail, ...prevPosts]);
    };

    window.addEventListener("newPostCreated", handleNewPost);

    return () => {
      window.removeEventListener("newPostCreated", handleNewPost);
    };
  }, []);

  // Toggle published/unpublished status.
  const togglePublishStatus = async (postId, published) => {
    try {
      const response = await axios.put(
        `${API_BASE_URL}/posts/${postId}`,
        { published: !published },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          } 
        }
      );
      if (onPostUpdated) {
        onPostUpdated(postId, response.data);
      }
      // Update local posts state
      setPosts((prevPosts) =>
        prevPosts.map((post) => (post.id === postId ? response.data : post))
      );
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post status.');
    }
  };

  // Toggle the expansion of comments for a specific post.
  const toggleComments = (postId) => {
    setExpandedPosts((prevExpanded) =>
      prevExpanded.includes(postId)
        ? prevExpanded.filter((id) => id !== postId)
        : [...prevExpanded, postId]
    );
  };

  // Enable edit mode for a post.
  const handleEditClick = (post) => {
    setEditingPost(post.id);
    setEditValues({ title: post.title, content: post.content });
  };

  // Cancel editing.
  const cancelEdit = () => {
    setEditingPost(null);
    setEditValues({ title: '', content: '' });
  };

  // Handle changes in the edit form.
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditValues((prev) => ({ ...prev, [name]: value }));
  };

  // Submit updated post data.
  const handleEditSubmit = async (e, postId) => {
    e.preventDefault();
    try {
      const response = await axios.put(
        `${API_BASE_URL}/posts/${postId}`,
        {
          title: editValues.title,
          content: editValues.content,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );
      if (onPostUpdated) {
        onPostUpdated(postId, response.data);
      }
      // Update local posts state
      setPosts((prevPosts) =>
        prevPosts.map((post) => (post.id === postId ? response.data : post))
      );
      setEditingPost(null);
      setEditValues({ title: '', content: '' });
    } catch (error) {
      console.error('Error updating post:', error);
      alert('Failed to update post.');
    }
  };

  // Delete the post with a confirmation warning.
  const handleDelete = async (postId) => {
    try {
      // First, fetch the post details to check if it has any comments.
      const response = await axios.get(`${API_BASE_URL}/posts/${postId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const post = response.data;

      if (post.comments && post.comments.length > 0) {
        alert(
          'You cannot delete this post because it has related comments. If you still want to delete this post, please delete the comments first.'
        );
        return; // Prevent deletion if there are related comments.
      } else {
        const confirmDelete = window.confirm(
          'Are you sure you want to delete this post? This action cannot be undone.'
        );
        if (!confirmDelete) return;
      }

      const deleteResponse = await axios.delete(
        `${API_BASE_URL}/posts/${postId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (deleteResponse.status === 204 || deleteResponse.status === 200) {
        if (onPostDeleted) {
          onPostDeleted(postId);
        }
        // Update local posts state
        setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId));
      } else {
        alert('Failed to delete post. Please try again.');
      }
    } catch (error) {
      console.error('Error deleting post:', error);
      alert('Failed to delete post.');
    }
  };

  return (
    <div>
      <h2>All Posts</h2>
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-card">
            {editingPost === post.id ? (
              <form
                onSubmit={(e) => handleEditSubmit(e, post.id)}
                className="post-edit-form"
              >
                <div>
                  <label>Title:</label>
                  <input
                    type="text"
                    name="title"
                    value={editValues.title}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div>
                  <label>Content:</label>
                  <textarea
                    name="content"
                    value={editValues.content}
                    onChange={handleEditChange}
                    rows="5"
                    required
                  ></textarea>
                </div>
                <button type="submit" className="btn">
                  Save
                </button>
                <button type="button" className="btn" onClick={cancelEdit}>
                  Cancel
                </button>
              </form>
            ) : (
              <>
                <h3>{post.title}</h3>
                <div
                  className="post-content"
                  dangerouslySetInnerHTML={{ __html: post.content }}
                />
                <p className="post-meta">
                  <strong>Author:</strong> {post.authorId} | <strong>Status:</strong>{' '}
                  <span
                    style={{
                      color: post.published
                        ? 'var(--success-color)'
                        : 'var(--danger-color)',
                      fontWeight: 'bold',
                    }}
                  >
                    {post.published ? 'Published' : 'Unpublished'}
                  </span>
                </p>
                <button
                  onClick={() => togglePublishStatus(post.id, post.published)}
                  className={
                    post.published ? 'btn btn-unpublish' : 'btn btn-publish'
                  }
                >
                  {post.published ? 'Unpublish' : 'Publish'}
                </button>
                <button onClick={() => handleEditClick(post)} className="btn btn-edit">
                  Edit
                </button>
                <button onClick={() => handleDelete(post.id)} className="btn btn-delete">
                  Delete
                </button>
                <div className="comment-toggle-container">
                  <span className="comment-count">
                    Comments {post.comments ? post.comments.length : 0}
                  </span>
                  {post.comments && post.comments.length > 0 && (
                    <button
                      onClick={() => toggleComments(post.id)}
                      className="comment-toggle-btn"
                    >
                      {expandedPosts.includes(post.id) ? 'Hide' : 'Show'}
                    </button>
                  )}
                </div>
                {expandedPosts.includes(post.id) && post.comments && (
                  <div className="comment-list">
                    <CommentList postId={post.id} />
                  </div>
                )}
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
